/*Table structure for table `book` */

DROP TABLE IF EXISTS `book`;

CREATE TABLE `book` (
  `book_id` int(11) NOT NULL AUTO_INCREMENT,
  `book_codigo` int(11) NOT NULL,
  `book_title` varchar(60) CHARACTER SET latin1 NOT NULL,
  `book_description` varchar(200) CHARACTER SET latin1 NOT NULL,
  `book_category` varchar(50) CHARACTER SET latin1 NOT NULL,
  `book_author` varchar(60) CHARACTER SET latin1 NOT NULL,
  `date_publish` date NOT NULL,
  `qty` int(11) NOT NULL,
  PRIMARY KEY (`book_id`),
  UNIQUE KEY `book_codigo` (`book_codigo`)
) ENGINE=InnoDB AUTO_INCREMENT=125 DEFAULT CHARSET=utf8;

