[vc_row full_width="stretch_row" css=".vc_custom_1508848182497{padding-top: 0px !important;padding-bottom: 0px !important;background-color: #FFFFFF !important;}"][vc_column][vc_column_text]
<div id="qx-dropcap-38561" class="qx-element qx-element-dropcap ">

<strong><span style="color: #d8ab0a; float: left; font-family: Times New Roman; font-size: 100px; line-height: 80px; padding-right: 5px; padding-top: 5px;"><a href="http://192.241.133.113/lemas1/wp-content/uploads/2019/08/logo-1.jpg"><img class="alignnone wp-image-2855" src="http://192.241.133.113/lemas1/wp-content/uploads/2019/08/logo-1.jpg" alt="" width="750" height="750" /></a></span></strong>

